import { useState } from "react";
import { useNavigate, useParams } from "react-router";
import { dashboard } from "../../routes";
import apiEndpoints from "../path/to/apiEndpoints";
function RentNowForm() {
    const navigate = useNavigate();
    const { carId } = useParams<{ carId: string }>();
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [summary, setSummary] = useState<any>(null);
    // const [customerId, setCustomerId] = useState(""); 
    if (!carId) {
        navigate(dashboard);
        return null;
    }

    const handleCheckAvailability = async () => {
        const response = await fetch("http://localhost:3000/booking/summary", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                // customer_id: customerId,
                car_id: parseInt(carId),
                start_date: startDate,
                end_date: endDate
            })
        });
        const result = await response.json();
        if (response.ok) {
            setSummary(result);
        } else {
            alert(result.message || "Booking check failed.");
        }
    };

    const handleConfirmBooking = async () => {
        const response = await fetch("http://localhost:3000/booking/confirm", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                // customer_id: customerId,
                car_id: parseInt(carId),
                start_date: startDate,
                end_date: endDate
            })
        });

        if (response.ok) {
            alert("Booking confirmed!");
            navigate(dashboard);
        } else {
            const error = await response.json();
            alert(error.message || "Booking failed.");
        }
    };

    return (
        <div className="app-container">
            <h1>Rent Now - Car ID: {carId}</h1>

            {/* <label>
                Customer ID:
                <input value={customerId} onChange={(e) => setCustomerId(e.target.value)} />
            </label> */}

            <label>
                Start Date:
                <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
            </label>

            <label>
                End Date:
                <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
            </label>

            <button onClick={handleCheckAvailability}>Check Availability & Price</button>

            {summary && (
                <div>
                    <h2>Summary</h2>
                    <p>Customer: {summary.customer_name}</p>
                    <p>Total Payment: ${summary.total_payment}</p>
                    <p>Insurance: ${summary.insurance_val}</p>
                    {summary.discount_price && <p>Discounted Daily Rate: ${summary.discount_price}</p>}
                    <button onClick={handleConfirmBooking}>Confirm Booking</button>
                </div>
            )}

            <button onClick={() => navigate(dashboard)}>Back to Dashboard</button>
        </div>
    );
}

