import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import apiEndpoints from '../../data/environment';
import { dashboard } from '../../routes';

import { useNavigate } from "react-router-dom";
const ReviewForm: React.FC = () => {
    const navigate = useNavigate();
    const [rating, setRating] = useState<number>(5);
    const [review, setReview] = useState<string>('');
    const [isSubmitting, setIsSubmitting] = useState<boolean>(false);
    const { booking_Id } = useParams<{ booking_id: string }>();
    console.log("booking_Id", booking_Id);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        if (!review.trim()) {
            alert('Please add a review comment');
            return;
        }

        setIsSubmitting(true);

        try {
            // Using the modified onAddReview function that accepts rating and review as parameters
            // await onAddReview(booking, rating, review);
            // Reset form after successful submission
            setRating(5);
            setReview('');
        } catch (error) {
            console.error('Error submitting review:', error);
        } finally {
            setIsSubmitting(false);
        }
    };
const callfetch = async () => {
    const response = await fetch(apiEndpoints.add_review, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
            booking_id: parseInt(booking_Id || '0'),
            rating: rating,
            review: review
        })
    });

    if (response.ok) {
        alert("Booking confirmed!");
        navigate(dashboard);
    } else {
        const error = await response.json();
        alert(error.message || "Booking failed.");
    }
}

    return (
        <div className="bg-white shadow rounded-lg p-6 max-w-md mx-auto">
            <h2 className="text-xl font-semibold mb-4">Add Your Review</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Rating</label>
                    <div className="flex space-x-2">
                        {[1, 2, 3, 4, 5].map((star) => (
                            <button
                                key={star}
                                type="button"
                                onClick={() => setRating(star)}
                                className="focus:outline-none"
                            >
                                <svg
                                    className={`w-8 h-8 ${star <= rating ? 'text-yellow-400' : 'text-gray-300'
                                        }`}
                                    fill="currentColor"
                                    viewBox="0 0 20 20"
                                    xmlns="http://www.w3.org/2000/svg"
                                >
                                    <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                                </svg>
                            </button>
                        ))}
                        <span className="ml-2 text-gray-600">{rating} out of 5</span>
                    </div>
                </div>

                <div className="mb-4">
                    <label htmlFor="review" className="block text-gray-700 mb-2">
                        Your Review
                    </label>
                    <textarea
                        id="review"
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        rows={4}
                        placeholder="Share your experience..."
                        value={review}
                        onChange={(e) => setReview(e.target.value)}
                        required
                    ></textarea>
                </div>

                <button
                    onClick={() => callfetch()}
                    type="submit"
                    disabled={isSubmitting}
                    className="w-full bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:bg-blue-300"
                >
                    {isSubmitting ? 'Submitting...' : 'Submit Review'}
                </button>
            </form>
        </div>
    );
};

export default ReviewForm;
